import { useState } from "react";

export default function App() {
  const [items, setItems] = useState([]);
  const numItems = items.length;
  console.log(numItems);

  function handleAddItems(item) {
    setItems([...items, item]);
  }

  function handleDeleteItems(id) {
    setItems(items.filter((item) => id !== item.id));
  }

  function clearList() {
    const confirm = window.confirm(
      "Are you sure you want to clear all the list?"
    );

    if (confirm) {
      setItems([]);
    }
  }
  function handleToggleItems(id) {
    setItems(
      items.map((item) =>
        item.id === id ? { ...item, packed: !item.packed } : item
      )
    );
  }
  return (
    <div className="app">
      <Logo />
      <Form onAddItems={handleAddItems} />
      <PackingList
        onToggleItems={handleToggleItems}
        onDeleteItems={handleDeleteItems}
        clearList={clearList}
        items={items}
      />
      <Stats items={items} />
    </div>
  );
}

function Logo() {
  return <h1>Far Away</h1>;
}

function Form({ onAddItems }) {
  const [description, setDescription] = useState("");
  const [select, setSelect] = useState(1);

  function formSubmit(e) {
    e.preventDefault();

    if (!description) return;
    const newItem = { description, select, packed: false, id: Date.now() };

    setDescription("");
    setSelect(1);

    console.log(newItem);
    onAddItems(newItem);
  }

  return (
    <form onSubmit={formSubmit} className="add-form">
      <h3>What do you need for your trip?</h3>
      <select
        value={select}
        onChange={(e) => setSelect(Number(e.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Item..."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <button type="submitt">Add</button>
    </form>
  );
}

function PackingList({ items, onDeleteItems, onToggleItems, clearList }) {
  const [sortBy, setSortBy] = useState("input");
  let sortedItem;
  if (sortBy === "input") {
    sortedItem = items;
  } else if (sortBy === "description") {
    sortedItem = items
      .slice()
      .sort((a, b) => a.description.localeCompare(b.description));
  } else if (sortBy === "packed") {
    sortedItem = items
      .slice()
      .sort((a, b) => Number(a.packed) - Number(b.packed));
  }

  return (
    <div className="list">
      <ul>
        {sortedItem.map((item) => (
          <Item
            item={item}
            onToggleItems={onToggleItems}
            onDeleteItems={onDeleteItems}
            key={item.id}
          />
        ))}
      </ul>
      <div className="options">
        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value={"input"}>Sort by intput order</option>
          <option value={"description"}>Sort by description</option>
          <option value={"packed"}>Sort by packed stats</option>
        </select>
        <button onClick={clearList}>Clear list</button>
      </div>
    </div>
  );
}

function Item({ item, onDeleteItems, onToggleItems }) {
  return (
    <li>
      <input
        type="checkbox"
        value={item.packed}
        onChange={() => onToggleItems(item.id)}
      />
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.select} &nbsp;
        {item.description}
      </span>
      <button onClick={() => onDeleteItems(item.id)}> ❌ </button>
    </li>
  );
}

function Stats({ items }) {
  const numPacked = items.filter((item) => item.packed);
  console.log(numPacked);
  return (
    <footer className="stats">
      <em>
        you have {items.length} items on your list, and you already packed
        {numPacked.length} items and i.e&nbsp;
        {items.length === 0
          ? 0
          : Math.round((numPacked.length / items.length) * 100)}
        %
      </em>
    </footer>
  );
}
